import React, { useState, useEffect } from 'react';
import '../App.css';

const SubmitNews = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
    category: '',
    tags: '',
    location: '',
    personsInvolved: '',
  });
  const [crimeEntries, setCrimeEntries] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
    fetch('http://localhost:5000/entries', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        setCrimeEntries([...crimeEntries, data]);
        setFormData({
          title: '',
          description: '',
          date: '',
          time: '',
          category: '',
          tags: '',
          location: '',
          personsInvolved: '',
        });
      })
      .catch((error) => console.error('Error:', error));
  };

  // Fetch entries on component mount
  useEffect(() => {
    fetch('http://localhost:5000/entries')
      .then((response) => response.json())
      .then((data) => setCrimeEntries(data))
      .catch((error) => console.error('Error fetching entries:', error));
  }, []);

  return (
    <div className="content-container">
      <div className="card">
        <h2>Submit News</h2>
        <form onSubmit={handleSubmit} className="form-container">
          <div className="form-field">
            <label>Title</label>
            <input
              type="text"
              name="title"
              placeholder="Enter title"
              value={formData.title}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-field">
            <label>Description</label>
            <textarea
              name="description"
              placeholder="Enter description"
              value={formData.description}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-field">
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-field">
            <label>Time</label>
            <input
              type="time"
              name="time"
              value={formData.time}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-field">
            <label>Category</label>
            <select name="category" value={formData.category} onChange={handleInputChange}>
              <option value="">Select Category</option>
              <option value="violent-crimes">Violent Crimes</option>
              <option value="property-crimes">Property Crimes</option>
              <option value="cyber-crimes">Cyber Crimes</option>
              <option value="drug-related-crimes">Drug-Related Crimes</option>
              <option value="white-collar-crimes">White-Collar Crimes</option>
            </select>
          </div>

          <div className="form-field">
            <label>Tags</label>
            <input
              type="text"
              name="tags"
              placeholder="Enter tags (e.g., theft, violence)"
              value={formData.tags}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-field">
            <label>Location</label>
            <input
              type="text"
              name="location"
              placeholder="Enter location"
              value={formData.location}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-field">
            <label>Persons Involved</label>
            <input
              type="number"
              name="personsInvolved"
              placeholder="Number of persons involved"
              value={formData.personsInvolved}
              onChange={handleInputChange}
            />
          </div>

          <button type="submit" className="submit-button">Submit</button>
        </form>
      </div>

      <div className="card">
        <h2>Crime Entries</h2>
        <table className="crime-table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Date</th>
              <th>Category</th>
              <th>Tags</th>
              <th>Location</th>
            </tr>
          </thead>
          <tbody>
            {crimeEntries.map((entry) => (
              <tr key={entry.id}>
                <td>{entry.title}</td>
                <td>{entry.description}</td>
                <td>{entry.date}</td>
                <td>{entry.category}</td>
                <td>{entry.tags}</td>
                <td>{entry.location}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SubmitNews;
