import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EntryDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [entry, setEntry] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/entries/${id}`)
      .then((response) => response.json())
      .then((data) => setEntry(data))
      .catch((error) => console.error('Error fetching entry details:', error));
  }, [id]);

  if (!entry) return <p>Loading...</p>;

  const navigateTo = (entryId) => {
    if (entryId) navigate(`/entry/${entryId}`);
  };

  return (
    <div className="entry-details">
      <h1>{entry.title}</h1>
      <p>{entry.description}</p>
      <p>Date: {entry.date}</p>
      <p>Category: {entry.category}</p>
      <p>Tags: {entry.tags}</p>
      <p>Location: {entry.location}</p>
      <p>Persons Involved: {entry.personsInvolved}</p>
      <div className="media-files">
        {entry.media_files.map((file, idx) => (
          <div key={idx}>
            {file.endsWith('.mp4') ? (
              <video controls src={`http://localhost:5000/media/${file}`} />
            ) : (
              <img src={`http://localhost:5000/media/${file}`} alt="media" />
            )}
          </div>
        ))}
      </div>
      <div className="navigation-buttons">
        <button onClick={() => navigateTo(entry.prev_id)} disabled={!entry.prev_id}>
          Previous
        </button>
        <button onClick={() => navigateTo(entry.next_id)} disabled={!entry.next_id}>
          Next
        </button>
      </div>
    </div>
  );
};

export default EntryDetails;
