import React, { useState, useEffect } from 'react';
import '../App.css';

const SubmitNews = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
    category: '',
    tags: '',
    location: '',
    personsInvolved: '',
  });
  const [crimeEntries, setCrimeEntries] = useState([]);

  // Fetch entries on component mount
  useEffect(() => {
    fetch('http://localhost:5000/entries')
      .then((response) => response.json())
      .then((data) => setCrimeEntries(data))
      .catch((error) => console.error('Error fetching entries:', error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/entries', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        setCrimeEntries([...crimeEntries, data]);
        setFormData({
          title: '',
          description: '',
          date: '',
          time: '',
          category: '',
          tags: '',
          location: '',
          personsInvolved: '',
        });
      })
      .catch((error) => console.error('Error:', error));
  };

  return (
    <div className="content-container">
      <div className="card">
        <h2>Submit News</h2>
        <form onSubmit={handleSubmit} className="form-container">
          {/* Form fields */}
          {/* Example Field */}
          <label>Title</label>
          <input type="text" placeholder="Title" value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })} />
          {/* Add other form fields similar to above */}
          <button type="submit" className="submit-button">Submit News</button>
        </form>
      </div>
      <div className="card">
        <h2>Crime Entries</h2>
        <table className="crime-table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Date</th>
              <th>Category</th>
              <th>Tags</th>
              <th>Location</th>
            </tr>
          </thead>
          <tbody>
            {crimeEntries.map((entry) => (
              <tr key={entry.id}>
                <td>{entry.title}</td>
                <td>{entry.description}</td>
                <td>{entry.date}</td>
                <td>{entry.category}</td>
                <td>{entry.tags}</td>
                <td>{entry.location}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SubmitNews;
