import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import './App.css';

const App = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
    category: '',
    tags: '',
    location: '',
    personsInvolved: '',
    media: [],
  });

  const [errors, setErrors] = useState({});
  const [crimeEntries, setCrimeEntries] = useState([]);
  const [filteredEntries, setFilteredEntries] = useState([]);
  const [search, setSearch] = useState({ category: '', tags: '' });

  // Fetch entries from backend on component mount
  useEffect(() => {
    fetch('http://localhost:5000/entries')
      .then((response) => response.json())
      .then((data) => {
        setCrimeEntries(data);
        setFilteredEntries(data);
      })
      .catch((error) => console.error('Error fetching entries:', error));
  }, []);

  // Filter entries based on search criteria
  useEffect(() => {
    const filtered = crimeEntries.filter(entry => 
      (search.category === '' || entry.category === search.category) &&
      (search.tags === '' || entry.tags.includes(search.tags))
    );
    setFilteredEntries(filtered);
  }, [search, crimeEntries]);

  // Validation functions
  const isValidText = (text) => /^[a-zA-Z\s]+$/.test(text); // Letters and spaces only
  const isValidTags = (tags) => /^[a-zA-Z\s,]+$/.test(tags); // Letters, spaces, and commas for tags
  const isValidTime = (time) => /^([01]\d|2[0-3]):([0-5]\d)$/.test(time); // 24-hour time format HH:MM
  const isPositiveInteger = (number) => /^\d+$/.test(number) && parseInt(number) > 0;

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title) newErrors.title = 'Title is required';
    if (!formData.description) newErrors.description = 'Description is required';
    if (!formData.date) newErrors.date = 'Date is required';
    if (!formData.time) newErrors.time = 'Time is required';
    if (!formData.category) newErrors.category = 'Category is required';
    if (!formData.location) newErrors.location = 'Location is required';
    if (!formData.personsInvolved) newErrors.personsInvolved = 'Persons involved is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // If no errors, return true
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      fetch('http://localhost:5000/entries', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      })
        .then((response) => response.json())
        .then((data) => {
          alert('News successfully submitted!');
          setCrimeEntries([...crimeEntries, data]);
          setFilteredEntries([...crimeEntries, data]);
          setFormData({
            title: '',
            description: '',
            date: '',
            time: '',
            category: '',
            tags: '',
            location: '',
            personsInvolved: '',
            media: [],
          });
        })
        .catch((error) => console.error('Error:', error));
    } else {
      console.log('Validation failed');
    }
  };

  // Handle filtering changes
  const handleSearchChange = (e) => {
    const { name, value } = e.target;
    setSearch({ ...search, [name]: value });
  };

  // Export table data to PDF
  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.autoTable({ html: '#crime-table' });
    doc.save('CrimeEntries.pdf');
  };

  return (
    <motion.div
      className="app-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <h1>Crime News History Documentation</h1>
      <form onSubmit={handleSubmit} className="form-container">
        {/* Form Fields */}
        <label>Title</label>
        <input
          type="text"
          placeholder="Title"
          value={formData.title}
          onChange={(e) => setFormData({ ...formData, title: e.target.value })}
        />
        {/* More form fields */}
        <button type="submit" className="submit-button">Submit News</button>
      </form>

      {/* Search Filters */}
      <div className="filter-container">
        <label>Category:</label>
        <select
          name="category"
          value={search.category}
          onChange={handleSearchChange}
        >
          <option value="">All Categories</option>
          <option value="violent-crimes">Violent Crimes</option>
          <option value="property-crimes">Property Crimes</option>
          {/* More categories */}
        </select>

        <label>Tags:</label>
        <input
          type="text"
          name="tags"
          placeholder="Tags"
          value={search.tags}
          onChange={handleSearchChange}
        />
        <button onClick={exportToPDF} className="export-button">Export to PDF</button>
      </div>

      {/* Table to Display Entries */}
      <h2>Crime Entries</h2>
      <table id="crime-table" className="crime-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Date</th>
            <th>Category</th>
            <th>Tags</th>
            <th>Location</th>
          </tr>
        </thead>
        <tbody>
          {filteredEntries.map((entry) => (
            <tr key={entry.id}>
              <td>{entry.title}</td>
              <td>{entry.description}</td>
              <td>{entry.date}</td>
              <td>{entry.category}</td>
              <td>{entry.tags}</td>
              <td>{entry.location}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </motion.div>
  );
};

export default App;
