import React, { useState } from 'react';
import '../App.css';

const SubmitNews = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
    category: '',
    tags: '',
    location: '',
    personsInvolved: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
    // Send formData to the backend
  };

  return (
    <div className="submit-container">
      <h2>Submit News</h2>
      <form onSubmit={handleSubmit} className="styled-form">
        <label>Title</label>
        <input
          type="text"
          name="title"
          placeholder="Enter title"
          value={formData.title}
          onChange={handleInputChange}
        />
        
        <label>Description</label>
        <textarea
          name="description"
          placeholder="Enter description"
          value={formData.description}
          onChange={handleInputChange}
        />

        <label>Date</label>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleInputChange}
        />

        <label>Time</label>
        <input
          type="time"
          name="time"
          value={formData.time}
          onChange={handleInputChange}
        />

        <label>Category</label>
        <select name="category" value={formData.category} onChange={handleInputChange}>
          <option value="">Select Category</option>
          <option value="violent-crimes">Violent Crimes</option>
          <option value="property-crimes">Property Crimes</option>
          <option value="cyber-crimes">Cyber Crimes</option>
          <option value="drug-related-crimes">Drug-Related Crimes</option>
          <option value="white-collar-crimes">White-Collar Crimes</option>
        </select>

        <label>Tags</label>
        <input
          type="text"
          name="tags"
          placeholder="Enter tags (e.g., theft, violence)"
          value={formData.tags}
          onChange={handleInputChange}
        />

        <label>Location</label>
        <input
          type="text"
          name="location"
          placeholder="Enter location"
          value={formData.location}
          onChange={handleInputChange}
        />

        <label>Persons Involved</label>
        <input
          type="number"
          name="personsInvolved"
          placeholder="Number of persons involved"
          value={formData.personsInvolved}
          onChange={handleInputChange}
        />

        <button type="submit" className="submit-button">Submit</button>
      </form>
    </div>
  );
};

export default SubmitNews;
