import React, { useState } from 'react'

const FormWithoutYup = () => {

    const [formData, setFormData] =useState({
        firstName:'',
        lastName:'',
        email:'',
        phoneNumber:'',
        address:'',
        birthDate:'',
        gender:'',
    });
    const [errors, setErrors] = useState();

  const isValidEmail = (email) => {
    // Regular expression for basic email validation
    const emailRegex = /^\S+@\S+\.\S+$/;
    return emailRegex.test(email);
  };

  const isValidPhoneNumber = (phoneNumber) => {
    // Regular expression for basic phone number validation (10 digits)
    const phoneRegex = /^\d{10}$/;
    return phoneRegex.test(phoneNumber);
  };

  const isValidPassword = (password) => {
    // Regular expressions for password validation
    const symbolRegex = /[!@#$%^&*(),.?":{}|<>]/;
    const numberRegex = /[0-9]/;
    const upperCaseRegex = /[A-Z]/;
    const lowerCaseRegex = /[a-z]/;
    return (
      password.length >= 8 &&
      symbolRegex.test(password) &&
      numberRegex.test(password) &&
      upperCaseRegex.test(password) &&
      lowerCaseRegex.test(password)
    );
  };

  const isValidAge = (age) => {
    return parseInt(age) >= 18 && parseInt(age) <= 100;
  };

  const validateForm = () => {
    let newErrors = {};

    if (!formData.firstName) {
      newErrors.firstName = "First name is required";
    }
    if (!formData.lastName) {
      newErrors.lastName = "Last name is required";
    }
    if (!formData.email) {
      newErrors.email = "Email is required";
    } else if (!isValidEmail(formData.email)) {
      newErrors.email = "Invalid email format";
    }
    if (!formData.phoneNumber) {
      newErrors.phoneNumber = "Phone number is required";
    } else if (!isValidPhoneNumber(formData.phoneNumber)) {
      newErrors.phoneNumber = "Phone number must be 10 digits";
    }
    if (!formData.password) {
      newErrors.password = "Password is required";
    } else if (!isValidPassword(formData.password)) {
      newErrors.password =
        "Password must be at least 8 characters long and contain at least one symbol, one number, one uppercase letter, and one lowercase letter";
    }
    if (!formData.confirmPassword) {
      newErrors.confirmPassword = "Confirm password is required";
    } else if (formData.confirmPassword !== formData.password) {
      newErrors.confirmPassword = "Passwords must match";
    }
    if (!formData.age) {
      newErrors.age = "Age is required";
    } else if (!isValidAge(formData.age)) {
      newErrors.age =
        "You must be at least 18 years old and not older than 100 years";
    }
    if (!formData.gender) {
      newErrors.gender = "Gender is required";
    }
    if (formData.interests.length === 0) {
      newErrors.interests = "Select at least one interest";
    }
    if (!formData.birthDate) {
      newErrors.birthDate = "Date of birth is required";
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  console.log(errors);

  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validateForm();
    if (isValid) {
      console.log("Form Submitted", formData);
    } else {
      console.log("Form Validation Failed");
    }
  };

  

  const handleCheckboxChange = (e) => {
    const {name, checked} = e.target;
    let updatedInterests = [...formData.interests];
    if (checked) {
      updatedInterests.push(name);
    } else {
      updatedInterests = updatedInterests.filter(
        (interest) => interest !== name
      );
    }

    setFormData({
      ...formData,
      interests: updatedInterests,
    });
  };

    const handleChange=(e)=>{
        const {name,value}=e.target;
        setFormData({
            ...formData,
            [name]:value,
        });
    };
    return (
        <form className='form' onSubmit={handleSubmit}>
        
        <div>
            <label>First Name:</label>
            <input
            type='text'
            name='firstName'
            value={formData.firstName}
            placeholder='Enter your first name'
            onChange={handleChange}
            />
        </div>


            <div>
            <label>Last Name:</label>
            <input
            type='text'
            name='lastName'
            value={formData.lastName}
            placeholder='Enter your last name'
            onChange={handleChange}
            />
            </div>

            <div>
            <label>Email:</label>
            <input
            type='text'
            name='email'
            value={formData.email}
            placeholder='Enter your Email'
            onChange={handleChange}
            />
            </div>

            <div>
            <label>Phone Number:</label>
            <input
            type='text'
            name='phoneNumber'
            value={formData.phoneNumber}
            placeholder='Enter your Phone number'
            onChange={handleChange}
            />
            </div>

            <div>
            <label>Address:</label>
            <input
            type='text'
            name='address'
            value={formData.address}
            placeholder='Enter your Address'
            onChange={handleChange}
            />
            </div>

            <div>
            <label>Date of Birth:</label>
            <input
            type='date'
            name='birthDate'
            value={formData.birthDate}
            placeholder='Enter your date of Birth'
            onChange={handleChange}
            />
            </div>
            <div>
                <label>Gender:</label>
                <select name='gender' value={formData.gender} onChange={handleChange}>
                    <option value=''>Select Gender</option>
                    <option value='male'>Male</option>
                    <option value='female'>Female</option>
                    <option value='other'>Other</option>
                </select>
            </div>

            <button type='submit'>Submit</button>
        </form>
    );
};

export default FormWithoutYup
