import React, { useState } from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import '../App.css';

const FilterNews = () => {
  const [filters, setFilters] = useState({
    category: '',
    tags: '',
    date: '',
    location: '',
  });

  const [filteredEntries, setFilteredEntries] = useState([]); // Example data

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleFilterSubmit = () => {
    console.log('Filters applied:', filters);
    // Fetch filtered data based on `filters`
  };

  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.text('Filtered Crime Entries', 14, 10);
    doc.autoTable({
      head: [['Title', 'Description', 'Date', 'Category', 'Tags', 'Location']],
      body: filteredEntries.map(entry => [
        entry.title,
        entry.description,
        entry.date,
        entry.category,
        entry.tags,
        entry.location,
      ]),
      startY: 20,
    });
    doc.save('Filtered_CrimeEntries.pdf');
  };

  return (
    <div className="filter-container">
      <h2>Filter News</h2>
      <div className="filter-form">
        <label>Category:</label>
        <select name="category" value={filters.category} onChange={handleFilterChange}>
          <option value="">All Categories</option>
          <option value="violent-crimes">Violent Crimes</option>
          <option value="property-crimes">Property Crimes</option>
        </select>

        <label>Tags:</label>
        <input
          type="text"
          name="tags"
          placeholder="Enter tags"
          value={filters.tags}
          onChange={handleFilterChange}
        />

        <label>Date:</label>
        <input
          type="date"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
        />

        <label>Location:</label>
        <input
          type="text"
          name="location"
          placeholder="Enter location"
          value={filters.location}
          onChange={handleFilterChange}
        />

        <button onClick={handleFilterSubmit} className="filter-button">Apply Filters</button>
      </div>

      <h2>Filtered Crime Entries</h2>
      <table className="crime-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Date</th>
            <th>Category</th>
            <th>Tags</th>
            <th>Location</th>
          </tr>
        </thead>
        <tbody>
          {filteredEntries.map((entry, index) => (
            <tr key={index}>
              <td>{entry.title}</td>
              <td>{entry.description}</td>
              <td>{entry.date}</td>
              <td>{entry.category}</td>
              <td>{entry.tags}</td>
              <td>{entry.location}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={exportToPDF} className="export-button">Export to PDF</button>
    </div>
  );
};

export default FilterNews;
