import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './App.css'; // Import the CSS

const App = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
    category: '',
    tags: '',
    location: '',
    personsInvolved: '',
    media: [],
  });

  const [errors, setErrors] = useState({});

  // Validation functions
  const isValidText = (text) => /^[a-zA-Z\s]+$/.test(text); // Letters and spaces only
  const isValidTags = (tags) => /^[a-zA-Z\s,]+$/.test(tags); // Letters, spaces, and commas for tags
  const isValidTime = (time) => /^([01]\d|2[0-3]):([0-5]\d)$/.test(time); // 24-hour time format HH:MM
  const isPositiveInteger = (number) => /^\d+$/.test(number) && parseInt(number) > 0;

  // Date validation: check if the date is not in the future
  const isValidDate = (date) => {
    const inputDate = new Date(date);
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Set to start of today for accurate comparison
    return inputDate <= today; // Valid if input date is less than or equal to today
  };

  const validateForm = () => {
    const newErrors = {};

    // Validate title (letters and spaces only)
    if (!formData.title) {
      newErrors.title = 'Title is required';
    } else if (!isValidText(formData.title)) {
      newErrors.title = 'Title should only contain letters and spaces';
    }

    // Validate description (required)
    if (!formData.description) newErrors.description = 'Description is required';

    // Validate date (valid date and no future dates allowed)
    if (!formData.date) {
      newErrors.date = 'Date is required';
    } else if (!isValidDate(formData.date)) {
      newErrors.date = 'Date cannot be in the future';
    }

    // Validate time (24-hour format HH:MM)
    if (!formData.time) {
      newErrors.time = 'Time is required';
    } else if (!isValidTime(formData.time)) {
      newErrors.time = 'Time should be in HH:MM format';
    }

    // Validate category (must be selected)
    if (!formData.category) newErrors.category = 'Category is required';

    // Validate tags (optional, but if entered, must be letters, spaces, and commas)
    if (formData.tags && !isValidTags(formData.tags)) {
      newErrors.tags = 'Tags should only contain letters, spaces, and commas';
    }

    // Validate location (letters and spaces only)
    if (!formData.location) {
      newErrors.location = 'Location is required';
    } else if (!isValidText(formData.location)) {
      newErrors.location = 'Location should only contain letters and spaces';
    }

    // Validate number of persons involved (positive integer)
    if (!formData.personsInvolved) {
      newErrors.personsInvolved = 'Number of persons involved is required';
    } else if (!isPositiveInteger(formData.personsInvolved)) {
      newErrors.personsInvolved = 'Number of persons should be a positive integer';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // If no errors, return true
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Form data submitted: ', formData);
      alert('News successfully submitted!');
      // Reset form after successful submission (optional)
      setFormData({
        title: '',
        description: '',
        date: '',
        time: '',
        category: '',
        tags: '',
        location: '',
        personsInvolved: '',
        media: [],
      });
    } else {
      console.log('Validation failed');
    }
  };

  const handleMediaChange = (e) => {
    setFormData({ ...formData, media: Array.from(e.target.files) });
  };

  return (
    <motion.div
      className="app-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <h1>Crime News History Documentation</h1>
      <form onSubmit={handleSubmit} className="form-container">
        <label>Title</label>
        <input
          type="text"
          placeholder="Title"
          value={formData.title}
          onChange={(e) => setFormData({ ...formData, title: e.target.value })}
        />
        {errors.title && <span className="error">{errors.title}</span>}

        <label>Description</label>
        <textarea
          placeholder="Description"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
        />
        {errors.description && <span className="error">{errors.description}</span>}

        <label>Date of Event</label>
        <input
          type="date"
          value={formData.date}
          onChange={(e) => setFormData({ ...formData, date: e.target.value })}
        />
        {errors.date && <span className="error">{errors.date}</span>}

        <label>Time of Event (HH:MM)</label>
        <input
          type="time"
          value={formData.time}
          onChange={(e) => setFormData({ ...formData, time: e.target.value })}
        />
        {errors.time && <span className="error">{errors.time}</span>}

        <label>Category</label>
        <select
          value={formData.category}
          onChange={(e) => setFormData({ ...formData, category: e.target.value })}
        >
          <option value="">Select Category</option>
          <option value="violent-crimes">Violent Crimes</option>
          <option value="property-crimes">Property Crimes</option>
          <option value="white-collar-crimes">White-Collar Crimes</option>
          <option value="cyber-crimes">Cyber Crimes</option>
          <option value="drug-related-crimes">Drug-Related Crimes</option>
          <option value="organised-crime">Organised Crime</option>
          <option value="sexual-offenses">Sexual Offenses</option>
          <option value="hate-crimes">Hate Crimes</option>
          <option value="environmental-crimes">Environmental Crimes</option>
          <option value="terrorism">Terrorism and Extremism</option>
        </select>
        {errors.category && <span className="error">{errors.category}</span>}

        <label>Tags (e.g., Theft, Violence)</label>
        <input
          type="text"
          placeholder="Tags"
          value={formData.tags}
          onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
        />
        {errors.tags && <span className="error">{errors.tags}</span>}

        <label>Location</label>
        <input
          type="text"
          placeholder="Location"
          value={formData.location}
          onChange={(e) => setFormData({ ...formData, location: e.target.value })}
        />
        {errors.location && <span className="error">{errors.location}</span>}

        <label>Number of Persons Involved</label>
        <input
          type="number"
          placeholder="Number of Persons Involved"
          value={formData.personsInvolved}
          onChange={(e) => setFormData({ ...formData, personsInvolved: e.target.value })}
        />
        {errors.personsInvolved && <span className="error">{errors.personsInvolved}</span>}

        <label>Media Files (Optional)</label>
        <input
          type="file"
          multiple
          onChange={handleMediaChange}
          accept="image/*, video/*"
        />

        {/* Preview of uploaded media */}
        {formData.media.length > 0 && (
          <div className="image-preview">
            {formData.media.map((file, index) => (
              <img
                key={index}
                src={URL.createObjectURL(file)}
                alt="Preview"
                className="preview-img"
              />
            ))}
          </div>
        )}

        <button type="submit" className="submit-button">Submit News</button>
      </form>
    </motion.div>
  );
};

export default App;
